import React from 'react';

const RatingStars = ({ rating }) => {
    const maxStars = 10; // Jumlah maksimum bintang
    const roundedRating = Math.round(rating * 2) / 2; // Bulatkan nilai rating ke setengah

    // Membuat array dengan panjang sesuai jumlah bintang
    const starsArray = Array.from({ length: maxStars }, (_, index) => (
        <span key={index} className={index < roundedRating ? 'filled' : 'empty'}>
            {index + 0.5 === roundedRating ? '★' : '☆'} {/* Gunakan karakter setengah bintang jika index + 0.5 sama dengan roundedRating */}
        </span>
    ));

    return <div className="rating-stars">{starsArray}</div>;
};

export default RatingStars;
